import React,{useReducer} from 'react';
const reducer=(state,action)=>{
    switch(action.type){
        case "INC":
            return {count:state.count+1,showText:state.showText};
        case "tooggle":
            return {};
        default :
            return {};

    }
};
const ReducerDemo = () => {
    const [state,dispatch]=useReducer(reducer,{count:0,showText:true});
    return <div>
        <h3>User Reducer</h3>
        {state.count}
        <button></button>
    </div>;
}

export default ReducerDemo;
